import {BaseConfigurator} from './base_configurator';
import path from 'path';
import HapiBunyan from 'hapi-bunyan';
import poop from 'poop';
import logger from '@hoist/logger';
import good from 'good';
import goodConsole from 'good-console';
/* istanbul ignore next */
export class LoggingConfigurator extends BaseConfigurator {
  configure (hapiServer) {
    return Promise
      .resolve()
      .then(() => {
        return hapiServer.registerAsync({
          register: HapiBunyan,
          options: {
            logger: logger.child({cls: 'HAPI_CORE'})
          }
        });
      })
      .then(() => {
        return hapiServer.registerAsync({
          register: poop,
          options: {
            logPath: path.join(process.cwd(), 'poop.log')
          }
        });
      })
      .then(() => {
        return hapiServer.registerAsync({
          register: good,
          options: {
            opsInterval: 1000,
            reporters: [
              {
                reporter: goodConsole,
                events: {
                  log: '*',
                  response: '*'
                }
              }
            ]
          }
        });
      })
  }
}

'use strict';
import {BaseConfigurator} from './base_configurator';
import path from 'path';
import glob from 'glob';
import Bluebird from 'bluebird';
import {BaseController} from '../areas/base_controller';
import config from 'config';
import Boom from 'boom';
import {Session} from '@hoist/model';
let globAsync = Bluebird.promisify(glob);
/* istanbul ignore next */
export class RouteConfigurator extends BaseConfigurator {
  constructor() {
    super();
  }
  configure(hapiServer) {
    return this
      .mapControllerRoutes(hapiServer)
      .then(() => this.mapStaticRoutes(hapiServer))
      .then(() => this.mapDefaultRoute(hapiServer));
  }
  mapStaticRoutes(hapiServer) {
    return hapiServer.route([
      {
        method: 'GET',
        path: '/js/{param*}',
        handler: {
          directory: {
            path: path.resolve(process.cwd(), './lib/client'),
            redirectToSlash: true,
            index: false
          }
        }
      }, {
        method: 'GET',
        path: '/img/{param*}',
        handler: {
          directory: {
            path: path.resolve(process.cwd(), './lib/assets/img'),
            redirectToSlash: true,
            index: false
          }
        }
      }
    ]);
  }
  mapDefaultRoute(hapiServer) {
    return hapiServer.route([
      {
        method: [
          'GET', 'POST', 'PUT', 'PATCH', 'DELETE'
        ],
        path: '/api/{p*}',
        config: {
          handler: (request, reply) => {
            reply(Boom.notFound())
          }
        }
      }, {
        method: 'GET',
        path: '/{p*}',
        config: {
          auth: {
            mode: 'try',
            strategy: 'session'
          },
          plugins: {
            'hapi-auth-cookie': {
              redirectTo: false
            }
          },
          handler: (request, reply) => {
            return Promise
              .resolve()
              .then(() => {
                //should load session here
                return {};
              })
              .then((state) => {
                return state;
              })
              .then((state) => {
                let settings = {
                  domains: config.get('Hoist.domains')
                };
                reply.view('index', {
                  HoistConfig: config.get('Hoist'),
                  initialState: Object.assign({}, state, {
                    session: {
                      isValid: request.auth.isAuthenticated && !!(request.auth.credentials)
                    }
                  }, {settings})
                });
              })
              .catch((err) => {
                this
                  ._logger
                  .alert(err);
                this
                  ._logger
                  .error(err);
                reply(Boom.wrap(err));
              });
          }
        }
      }
    ])
  }
  mapControllerRoutes(hapiServer) {
    return globAsync('../areas/**/*_controller.js', {cwd: __dirname}).then((controllersPaths) => {
      return controllersPaths.filter((controllerPath) => {
        return !controllerPath.endsWith('base_controller.js');
      }).map((controllerPath) => {
        let Controller = require(controllerPath).default;
        return new Controller();
      });
    }).then((controllers) => {
      return Promise.all(controllers.filter((controller) => {
        return (controller instanceof BaseController);
      }).map((controller) => {
        return controller._loadRoutes(hapiServer);
      })).then(() => {});
    });

  }
}

'use strict';
import {BaseConfigurator} from './base_configurator';
import config from 'config';
import vision from 'vision';
//import WebpackConfig from '../../../config/webpack/webpack.config';
import handlebars from 'handlebars';
import path from 'path';
import inert from 'inert';
//const compiler = Webpack(WebpackConfig);
/* istanbul ignore next */
export class ServerConfigurator extends BaseConfigurator {
  constructor () {
    super();
  }
  configure (hapiServer) {
    return Promise
      .resolve()
      .then(() => this._configureMainConnection(hapiServer))
      .then(() => this._configureWebpack(hapiServer))
      .then(() => this._configureViewEngine(hapiServer))
  }

  _configureMainConnection (hapiServer) {
    return hapiServer.connection({host: '0.0.0.0', port: 8000});
  }
  _configureWebpack (hapiServer) {
    if (!config.get('Hoist.webpack.optimize')) {
      let Webpack = require('webpack');
      let webpackConfig = require('../../../config/webpack/webpack.config');
      let compiler = Webpack(webpackConfig);
      return hapiServer.registerAsync({
        register: require('hapi-webpack-plugin'),
        options: {
          compiler: compiler,
          hot: {},
          assets: webpackConfig.assets
        }
      });
      // Do "hot-reloading" of react stuff on the server
      // Throw away the cached client modules and let them be re-required next time
      compiler.plugin('done', function () {
        Object
          .keys(require.cache)
          .forEach(function (id) {
            if (/\/client\//.test(id))
              delete require.cache[id];
            }
          );
      });
    }
  }
  _configureViewEngine (hapiServer) {
    return hapiServer
      .registerAsync(vision)
      .then(() => {
        handlebars.registerHelper('json', JSON.stringify);
        return hapiServer.views({
          engines: {
            hbs: handlebars
          },
          isCached: false,
          relativeTo: path.resolve(__dirname, '../'),
          path: 'views'
        })
      })
      .then(() => hapiServer.registerAsync(inert));
  }
}

'use strict';
import {Server as HapiServer} from 'hapi';
import Bluebird from 'bluebird';
import configurators from './configuration';
import logger from '@hoist/logger';
import hapi from 'hapi';
import config from 'config';
import {_mongoose} from '@hoist/model';
_mongoose.set('debug', true);
/**
 * The main portal server itself
 */

export default class PortalServer {
  /**
   * create a new portal server
   */
  constructor () {
    this._logger = logger.child({cls: this.constructor.name});
  }
  _createServer () {
    return Promise
      .resolve()
      .then(() => {
        this._hapiServer = new HapiServer();
        Bluebird.promisifyAll(this._hapiServer);
      })
      .then(() => configurators.server.configure(this._hapiServer))
      .then(() => configurators.logging.configure(this._hapiServer))
      .then(() => configurators.auth.configure(this._hapiServer))
      .then(() => configurators.routes.configure(this._hapiServer))
      .then(() => this._hapiServer);
  }

  /**
   * start the server and connect to mongo
   * @returns {Promise} - for the server to have started
   */
  start () {
    return _mongoose
      .connectAsync(config.get('Hoist.mongo.core.connectionString'))
      .then(() => this._createServer())
      .then(() => this._hapiServer.startAsync())
      .then(() => {
        this
          ._logger
          .info({
            info: this._hapiServer.info
          }, 'server listening')
      })
      .catch((err) => {
        this
          ._logger
          .error(err);
        this
          ._logger
          .alert(err);
      });
  }
}

import amqp from 'amqplib';
import config from 'config';
import logger from '@hoist/logger';
import moment from 'moment';
const _logger = logger.child({cls: 'RabbitUtils'});

let _connection = amqp
  .connect(config.get('Hoist.rabbit.url'))
  .then((connection) => {
    connection.on('close', () => {
      open = null;
    });
    connection.on('error', (err) => {
      _logger.error(err);
      _logger.alert(err);
    });
    return connection;
  });

function connect() {
  if (!_connection) {
    _connection = amqp
      .connect(config.get('Hoist.rabbit.url'))
      .then((connection) => {
        connection.on('close', () => {
          _connection = null;
        });
        connection.on('error', (err) => {
          _logger.error(err);
          _logger.alert(err);
        });
        return connection;
      });
  }
  return _connection;
}

export function setupQueue({queue, exchange}) {
  return connect().then((connection) => {
    _logger.info('creating channel');
    return connection.createChannel()
  }).then((channel) => {
    let _err;
    _logger.info('asserting queue');
    return channel
      .assertQueue(queue.name, queue.properties)
      .then((queueDetails) => {
        if (exchange) {
          _logger.info('asserting exchange');
          return channel
            .assertExchange(exchange.name, exchange.type)
            .then(() => {
              _logger.info('binding queue to exchange');
              return channel.bindQueue(queueDetails.queue, exchange.name, exchange.routingKey);
            })
        }
      })
      .catch((err) => {
        _logger.info('saving error for later');
        _err = err;
      })
      .then(() => {
        _logger.info('closing channel');
        return channel.close();
      })
      .then(() => {
        if (_err) {
          _logger.info('rethrowing error');
          throw _err;
        }
      });
  });
}

function _getMany(channel, queue, max = 10, current = []) {
  _logger.info({
    current
  }, 'getting messages');
  if (current.length > max) {
    return current;
  } else {
    return channel
      .get(queue, {noAck: true})
      .then((message) => {
        if (!message) {
          return current;
        } else {
          let content = JSON.parse(message.content.toString());
          _logger.info({
            content
          }, 'got a rabbit message');
          return _getMany(channel, queue, max, current.concat(formatLogEntry(content)));
        }
      })
  }
}

function formatLogEntry(eventLog) {
  return {
    id: eventLog._id,
    type: eventLog.type || 'log',
    message: eventLog.message,
    time: moment(eventLog.createdAt).format('HH:mm:ss'),
    stack: eventLog.errorStack
  }
}
export function getMessages(queue, max) {
  return connect().then((connection) => {
    _logger.info('creating channel');
    return connection.createChannel()
  }).then((channel) => {
    let _err;
    channel.prefetch(max);
    return _getMany(channel, queue, max, []).catch((err) => {
      _logger.info('saving error for later');
      _err = err;
    }).then((messages) => {
      _logger.info('closing channel');
      return channel
        .close()
        .then(() => messages);
    }).then((messages) => {
      if (_err) {
        _logger.info('rethrowing error');
        throw _err;
      }
      return messages;
    });
  }).then((messages) => {
    _logger.info({
      messages: messages
    }, 'returning messages');
    return messages || [];
  });
}
export function closeConnection() {
  if (_connection) {
    return connect().then((connection) => {
      return connection.close();
    });
  }
}

export function sanitiseName(name) {
  var lowerCaseString = name.toLowerCase();
  return lowerCaseString.replace(/\W+/g, "");
}
